$(function(){sum(); $('.like').click(function(){var $display=$(this).css('color'); var file; if($display!=='rgb(255, 207, 191)'){$(this).siblings('i').css({'display':'inline-block', 'color':'#ffcfbf'}); $(this).css({'color':'#ffcfbf'}); file="add_fav";}else{$(this).siblings('i').css({'color':'white'}); $(this).css({'color':'black'}); file="remove_fav";}fav($(this).parent(),file);}); function fav($node,file){$product=$node.siblings(".caption").children('p').first().text().trim(); console.log($product); $.ajax({type:"GET", url:"data_handlers/"+file+".php", data: 'prod=' +$product, success: function(msg){console.log(msg);}});}$(".gallery-img").hover(function(){$(this).children("img").css("opacity","0.2"); $(this).children(".img-caption").css("display","block");},function(){$(this).children("img").css("opacity","1"); $(this).children(".img-caption").css("display","none");}); $("#contact-form button").click(function(){var isValid=true; var text=''; $('input, textarea').each(function(){if (($(this).val().trim()).length<1){isValid=false; $(this).after('<span class="error">'+$(this).attr('placeholder')+' required!</span>');}else{$(this).siblings('.error').remove();}}); var re=/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/; var isValidEmail=re.test($("#email").val()); if(isValidEmail){$(this).siblings('.error').remove();}else{$(this).after('<span class="error">Invalid email!</span>');}$.ajax({type: 'GET', url: 'send_mail.php', data: 'name='+$('#name').val()+'&email='+$('#email').val()+'&message='+$('#message').val(), success: function(msg){console.log(msg); if(msg.trim()==''){$("#error").hide(); pop_up("Enquiry submitted!");}else{$("#error").text(msg).show();}}});}); $('.add').click(function(){var val,text,node; var prod=$(this).prev().prev().text(); if($(this).prev().has('select').length!==0){node=$(this).prev().children('select'); val=node.val(); text=": "+node.children(":selected").text();}else{val=$(this).prev().children('input').val(); text=$(this).prev().text();}text=prod+" "+text.trim(); console.log(val); pop_up(text); $.ajax({type:"GET", url:"data_handlers/cart_add.php", data: 'value=' +val, success: function(msg){console.log(msg);}});}); function pop_up(text){$("#fill").text(text); $("#message-box").slideDown(1000); setTimeout(function(){$("#message-box").slideUp(1000);},3000);}$(".remove").click(function(){modify_cart($(this),0);}); function modify_cart(node,quant){var node=node.parent().siblings('input'); var cid=node.val(); $.ajax({type:"GET", url:"data_handlers/cart_change.php", data: 'cid='+cid+'&quant='+quant, success: function(msg){remove_node(node,quant);}});}function remove_node(node,quant){if(quant==0){node.parent().remove();}sum();}$(".plus").click(function(){quantity=parseInt(($(this).siblings('span').text()))+1; $(this).siblings('span').text(quantity); modify_cart($(this),quantity);}); $(".minus").click(function(){quantity=($(this).siblings('span').text())-1; $(this).siblings('span').text(quantity); modify_cart($(this),quantity);}); $("#submit").click(function(){let products=''; $(".item").each(function(){products+=','+$(this).children(".prod-name").text().trim();}); $('#products').val(products);}); function sum(){var sum=0; $(".item").each(function(){var cost=parseInt($(this).children('.cost').text()); var quant=parseInt($(this).children('.quant').children('span').text()); sum+=cost*quant;}); if(sum==0){$('#checkout').remove();}else{$('#sum').text(sum);}}$("#bars").click(function(e){e.preventDefault(); e.stopImmediatePropagation(); $("#v-menu").css("left","0");}); $("html").click(function(e){var $v_menu=$("#v-menu"); if(e.target!=$v_menu.get(0)){$("#v-menu").css("left","-52%");}var $overlay=$("#overlay"); if($overlay){$("#overlay").remove();}}); $("html").keyup(function(e){if (e.keyCode==27){ $("#overlay").remove();}}); $(".gallery-img").click(function(e){e.preventDefault(); e.stopImmediatePropagation(); var img_source=$(this).children('img').attr('src'); $img=$("<img src='"+img_source+"'>").css('opacity',0).load(display);}); function display(){$('<div id="overlay"><div id="photo"></div></div>').prependTo('body'); $photoDiv=$('#photo');$photoDiv.append($img); if($img.outerWidth()>=$(window).outerWidth() && $img.outerHeight()>=$(window).outerHeight()){if($img.outerWidth()<$img.outerHeight()){$img.css("height",(0.90*$(window).outerHeight()));}else{$img.css("width",(0.7*$(window).outerWidth()));}}if($img.outerHeight()>=$(window).outerHeight()){$img.css("height",(0.90*$(window).outerHeight()));}if($img.outerWidth()>=$(window).outerWidth()){$img.css("width",(0.75*$(window).outerWidth()));}$photoDiv.css('margin-left',(($(window).outerWidth()-$photoDiv.outerWidth())/2)); $photoDiv.css('margin-top',(($(window).outerHeight()-$photoDiv.outerHeight())/2)); $img.animate({opacity: 1}, 1000);}});